import {useState} from 'react'
import './FormComponent.css'

const FormComponent =()=>{
    const [name,setName] = useState('')
    const [tel,setTel] = useState('')

    const inputName=(Event)=>{
        setName(Event.target.value)
    }
    const inputTel=(Event)=>{
        setTel(Event.target.value)
    }
    const saveItem=(Event)=>{
        Event.preventDefault()
        console.log("save")
    }
    return(
        <div>
            <form onSubmit={saveItem}>
                <div className="form-control">
                    <label>กรุณากรอก ชื่อ-นามสกุลสมาชิก</label>
                    <input type="text" placehoder="ชื่อ-สกุล"  onChange={inputName}/>
                </div>
                <div>
                    <label>กรุณากรอก เบอร์โทรศัพท์สมาชิก</label>
                    <input type="text" placehoder="XXX-XXXX-XXXX" onChange={inputTel}/>
                </div>
                <div>
                    <button type="submit" className="btn" >เพิ่มข้อมูล</button>
                </div>
            </form>
        </div>
    )
}

export default FormComponent
